import React from 'react';
import { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";

import NewClientForm from './NewClientForm';
import SigninForm from './SignInForm';
import ClientProfile from './ClientProfile'
import EditProfileForm from './EditProfileForm';
import TrainerCollection from './TrainerCollection';

function App() {

    const [currentClient, setCurrentClient] = useState(null);
    const [showSigninForm, setShowSigninForm] = useState(false);
    const [trainers, setTrainers] = useState([])


    useEffect(() => {
        fetch("/trainers")
        .then(response => {
            if (response.ok) {
                response.json()
                .then(data => {
                    setTrainers(data)
                })
            }
        })
    }, [])

    const saveClient = (new_client) => {
        setCurrentClient(new_client);
    };

    const handleToggleForm = () => {
        setShowSigninForm(value => !value);
    };

    const handleEditProfileClick = () => {
        history.push(`/clients/${id}/edit-profile`);
      };

    useEffect(() => {
        fetch("/check-user")
        .then(response => {
          if (response.ok){
            response.json()
            .then(saveClient)
          }
        })
    }, [])

    const handleSignOutClick= () => {
        fetch("/signout", {method: "DELETE"})
        .then(() => {
        setCurrentClient(null);   
        },);
    }

    if (!currentClient) {
        return (
            <>
                {showSigninForm ? <SigninForm saveClient={saveClient} handleToggleForm={handleToggleForm} /> : <NewClientForm saveClient={saveClient} handleToggleForm={handleToggleForm} />}
            </>
        );
    }

    return (
        <div>
            <Switch>
                <Route exact path ="/">
                    <ClientProfile handleSignOutClick={handleSignOutClick} currentClient={currentClient} saveClient={saveClient} handleEditProfileClick={handleEditProfileClick}/>
                </Route>
                <Route path = "/clients/:id/edit-profile">
                    <EditProfileForm currentClient={currentClient} saveClient={saveClient}/>
                </Route>
                <Route path = "/trainers">
                    <TrainerCollection trainers={trainers}/>
                </Route>
            </Switch>
        </div>
    );
}

export default App;
