import React from 'react'
import {useFormik} from "formik";
import * as yup from "yup";
import { TextField, Button, Typography } from '@mui/material';

function NewClientForm({saveClient, handleToggleForm}) {

    const userSchema = yup.object({
      first_name: yup.string().required("Must enter a first name"),
      last_name: yup.string().required("Must enter a last name"),
      email: yup.string().required("Must enter an email"),
      password: yup.string()
        .required("Please enter your password")
        .min(8, "Password must contain at least 8 characters"),
      user_name: yup.string()
        .required("Please enter your user name")
        .min(4, "Username must contain at least 4 characters")
        .max(10, "Username length should be 10 characters maximum"),
      age: yup
        .number()
        .required("Please enter your age")
        .min(18, "You must be at least 18 years old to sign up")
        .max(90, "Age cannot exceed 90 years old"),
    });
      const formik = useFormik ({
          initialValues: {
              first_name: "",
              last_name: "",
              email: "",
              password: "",
          },
          validationSchema: userSchema,
          onSubmit: values => {
              fetch("/api/v1/signup", {
                  method:"POST",
                  headers: {
                      "Content-Type": "application/json",   
                  },
                  body: JSON.stringify(values, null, 2),
              }).then(resp => {
                  console.log("RESP", resp)
                  if (resp.ok) {
                      resp.json()
                      .then(client => {
                          saveClient(client)
                      })
                  }
                  else {
                      resp.json()
                      .then(error=> {
                          alert(error.error)
                      })
                  }
              })
          },
      });



        return (
            <div className="signup-form">
            <Typography variant="h4" component="h2">Sign Up</Typography>
            <form onSubmit={formik.handleSubmit}>
            <TextField
              id="first_name"
              name="first_name"
              label="First Name"
              variant="outlined"
              fullWidth
              onChange={formik.handleChange}
              value={formik.values.first_name}
              error={formik.touched.first_name && formik.errors.first_name}
              helperText={formik.touched.first_name && formik.errors.first_name}
            />

            <TextField
              id="last_name"
              name="last_name"
              label="Last Name"
              variant="outlined"
              fullWidth
              onChange={formik.handleChange}
              value={formik.values.last_name}
              error={formik.touched.last_name && formik.errors.last_name}
              helperText={formik.touched.last_name && formik.errors.last_name}
            />

            <TextField
              id="email"
              name="email"
              label="Email"
              variant="outlined"
              fullWidth
              onChange={formik.handleChange}
              value={formik.values.email}
              error={formik.touched.email && formik.errors.email}
              helperText={formik.touched.email && formik.errors.email}
            />

            <TextField
              id="password"
              name="password"
              label="Password"
              type="password"
              variant="outlined"
              fullWidth
              onChange={formik.handleChange}
              value={formik.values.password}
              error={formik.touched.password && formik.errors.password}
              helperText={formik.touched.password && formik.errors.password}
            />

            <Button type="submit" variant="contained" color="red">Submit</Button>
          </form>
          <Button onClick={handleToggleForm} variant="text">
            Existing user? Click here to login
          </Button>
        </div>
        );
      }

      export default NewClientForm;
