import React, { useState, useEffect } from 'react';
import { useFormik } from 'formik';
import * as yup from 'yup';
import { Button, FormControl, InputLabel, MenuItem, Select, Typography } from '@mui/material';
import { useHistory } from 'react-router-dom';

function NewRoutineForm({ currentClient, handleCreateRoutineClick, workouts, trainers, exercises }) {
  const history = useHistory();
  const [currentWorkouts, setCurrentWorkouts] = useState([]);
  const [currentExercises, setCurrentExercises] = useState([]);

  const routineSchema = yup.object({
    workout_id: yup.number().required('Workout is required'),
    exercise_id: yup.number().required('Exercise is required'),
  });

  const formik = useFormik({
    initialValues: {
      workout_id: '',
      exercise_id: '',
    },
    validationSchema: routineSchema,
    onSubmit: (values) => {
      console.log('Routine form submitted:', values);
      // Send POST request to create a new routine
      fetch('/routines', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          client_id: currentClient.id,
          workout_id: values.workout_id,
          exercise_id: values.exercise_id,
        }),
      })
        .then((response) => {
          if (response.ok) {
            console.log('Routine created successfully');
            // Redirect to the desired page
            history.push('/dashboard');
          } else {
            console.error('Failed to create routine');
          }
        })
        .catch((error) => {
          console.error('An error occurred while creating routine:', error);
        });
    },
  });

  useEffect(() => {
    // Fetch workouts and exercises based on the current client's ID
    fetch(`/workouts?clientId=${currentClient.id}`)
      .then((response) => response.json())
      .then((data) => setCurrentWorkouts(data))
      .catch((error) => console.error('Error fetching workouts:', error));

    fetch(`/exercises?clientId=${currentClient.id}`)
      .then((response) => response.json())
      .then((data) => setCurrentExercises(data))
      .catch((error) => console.error('Error fetching exercises:', error));
  }, [currentClient.id]);

  return (
    <div className="routine-form">
      <Typography variant="h4" component="h2">Create Routine</Typography>
      <form onSubmit={formik.handleSubmit}>
        <div className="form-group">
          <FormControl fullWidth>
            <InputLabel id="workout_id-label">Workout</InputLabel>
            <Select
              labelId="workout_id-label"
              id="workout_id"
              name="workout_id"
              value={formik.values.workout_id}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              error={formik.touched.workout_id && Boolean(formik.errors.workout_id)}
              fullWidth
            >
              <MenuItem value="" disabled>Select a workout</MenuItem>
              {workouts.map((workout) => (
                <MenuItem key={workout.id} value={workout.id}>{workout.name}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
        <div className="form-group">
          <FormControl fullWidth>
            <InputLabel id="exerciseId-label">Exercise</InputLabel>
            <Select
              labelId="exercise_id-label"
              id="exercise_id"
              name="exercise_id"
              value={formik.values.exercise_id}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              error={formik.touched.exercise_id && Boolean(formik.errors.exercise_id)}
              fullWidth
            >
              <MenuItem value="" disabled>Select an exercise</MenuItem>
              {exercises.map((exercise) => (
                <MenuItem key={exercise.id} value={exercise.id}>{exercise.name}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
        <Button type="submit" variant="contained" color="primary">Submit</Button>
      </form>
    </div>
  );
}

export default NewRoutineForm;
