import React from 'react'
import {useFormik} from "formik";
import * as yup from "yup";

function NewClientForm({save}) {

    const userSchema = yup.object({
      first_name: yup.string().required("Must enter a first name"),
      last_name: yup.string().required("Must enter a last name"),
      email: yup.string().required("Must enter an email"),
      password: yup.string()
        .required("Please enter your password")
        .min(8, "Password must contain at least 8 characters"),
      user_name: yup.string()
        .required("Please enter your user name")
        .min(4, "Username must contain at least 4 characters")
        .max(10, "Username length should be 10 characters maximum"),
      age: yup
        .number()
        .required("Please enter your age")
        .min(18, "You must be at least 18 years old to sign up")
        .max(90, "Age cannot exceed 90 years old"),
    });
      const formik = useFormik ({
          initialValues: {
              first_name: "",
              last_name: "",
              email: "",
              password: "",
          },
          validationSchema: userSchema,
          onSubmit: values => {
              fetch("/api/v1/signup", {
                  method:"POST",
                  headers: {
                      "Content-Type": "application/json",   
                  },
                  body: JSON.stringify(values, null, 2),
              }).then(resp => {
                  console.log("RESP", resp)
                  if (resp.ok) {
                      resp.json()
                      .then(client => {
                          saveClient(client)
                      })
                  }
                  else {
                      resp.json()
                      .then(error=> {
                          alert(error.error)
                      })
                  }
              })
          },
      });



      return (
          <div>
            <form className="form-text" onSubmit={formik.handleSubmit}>
              <div>
                <label htmlFor="first_name">First Name:</label>
                <input
                  id="first_name"
                  name="first_name"
                  type="text"
                  onChange={formik.handleChange}
                  value={formik.values.first_name}

                />
                {formik.touched.first_name && formik.errors.first_name && (
                  <div className="error">{formik.errors.first_name}</div>
                )}
              </div>

              <div>
                <label htmlFor="last_name">Last Name:</label>
                <input
                  id="last_name"
                  name="last_name"
                  type="text"
                  onChange={formik.handleChange}
                  value={formik.values.last_name}
                />
                {formik.touched.last_name && formik.errors.last_name && (
                  <div className="error">{formik.errors.last_name}</div>
                )}
              </div>

              <div>
                <label htmlFor="email">Email:</label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  onChange={formik.handleChange}
                  value={formik.values.email}
                />
                {formik.touched.email && formik.errors.email && (
                  <div className="error">{formik.errors.email}</div>
                )}
              </div>

              <div>
                <label htmlFor="password">Password:</label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  onChange={formik.handleChange}
                  value={formik.values.password} 
                />
                {formik.touched.password && formik.errors.password && (
                  <div className="error">{formik.errors.password}</div>
                )}
              </div>

              <button type="submit">Submit</button>
            </form>
            <button onClick={handleToggleForm}>
              Existing user? Click here to login
            </button>
          </div>
        );
      }

      export default NewClientForm;
