import React, { useEffect, useRef, useState } from 'react';

function Chat() {
  const [messages, setMessages] = useState([]);
  const connectionRef = useRef(null);
  const nameInputRef = useRef(null);
  const messageInputRef = useRef(null);

  useEffect(() => {
    connectionRef.current = new WebSocket("ws://localhost:8080");

    connectionRef.current.onopen = () => {
      console.log("WebSocket is open now.");
    };

    connectionRef.current.onclose = () => {
      console.log("WebSocket is closed now.");
    };

    connectionRef.current.onerror = (error) => {
      console.error("WebSocket error observed:", error);
    };

    connectionRef.current.onmessage = (event) => {
      const receivedData = event.data;
      setMessages((prevMessages) => [...prevMessages, receivedData]);
    };

    return () => {
      // Close the WebSocket connection when the component unmounts
      connectionRef.current.close();
    };
  }, []);

  const handleSendMessage = () => {
    const name = nameInputRef.current.value;
    const message = messageInputRef.current.value;
    const composedMessage = `${name}: ${message}`;

    // Send composed message to the server
    connectionRef.current.send(composedMessage);

    // Clear input fields
    nameInputRef.current.value = "";
    messageInputRef.current.value = "";
  };

  return (
    <div>
      <div id="chat">
        {messages.map((message, index) => (
          <p key={index}>{message}</p>
        ))}
      </div>
      <input ref={nameInputRef} type="text" placeholder="name" />
      <input ref={messageInputRef} type="text" placeholder="message" />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
}

export default Chat;
