import React, { useEffect, useState } from 'react';
import { Button, FormControl, InputLabel, MenuItem, Select, Typography } from '@mui/material';
import { useHistory } from 'react-router-dom';

function NewRoutineForm({ currentClient, handleCreateRoutineClick, workouts, trainers, exercises }) {
  const history = useHistory();
  const [selectedWorkout, setSelectedWorkout] = useState('');
  const [selectedExercise, setSelectedExercise] = useState('');

  const handleCreateRoutine = () => {
    const routineData = {
      workout_id: selectedWorkout,
      exercise_id: selectedExercise,
    };

    // Perform the POST request to create the routine
    fetch('/routines', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(routineData),
    })
      .then((response) => {
        if (response.ok) {
          console.log('New routine created successfully');
          // Handle success, such as showing a success message or redirecting to a new page
          history.push('/workouts'); // Redirect to the workouts page after creating a routine
        } else {
          console.error('Failed to create routine');
        }
      })
      .catch((error) => {
        console.error('An error occurred while creating the routine:', error);
      });
  };

  useEffect(() => {
    // Fetch workouts based on the current client's ID
    fetch(`/workouts?clientId=${currentClient.id}`)
      .then((response) => response.json())
      .then((data) => {
        setWorkouts(data);
        setSelectedWorkout(data[0]?.id || '');
      })
      .catch((error) => console.error('Error fetching workouts:', error));

    // Fetch exercises based on the current client's ID
    fetch(`/exercises?clientId=${currentClient.id}`)
      .then((response) => response.json())
      .then((data) => {
        setExercises(data);
        setSelectedExercise(data[0]?.id || '');
      })
      .catch((error) => console.error('Error fetching exercises:', error));
  }, [currentClient.id]);

  return (
    <div className="new-routine-form">
      <Typography variant="h4" component="h2">Create Routine</Typography>
      <FormControl fullWidth>
        <InputLabel id="workout-label">Select Workout</InputLabel>
        <Select
          labelId="workout-label"
          id="workout"
          value={selectedWorkout}
          onChange={(event) => setSelectedWorkout(event.target.value)}
          required
        >
          {workouts.map((workout) => (
            <MenuItem key={workout.id} value={workout.id}>
              {workout.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl fullWidth>
        <InputLabel id="exercise-label">Select Exercise</InputLabel>
        <Select
          labelId="exercise-label"
          id="exercise"
          value={selectedExercise}
          onChange={(event) => setSelectedExercise(event.target.value)}
          required
        >
          {exercises.map((exercise) => (
            <MenuItem key={exercise.id} value={exercise.id}>
              {exercise.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <Button
        type="button"
        variant="contained"
        color="primary"
        onClick={handleCreateRoutine}
      >
        Create Routine
      </Button>
    </div>
  );
}

export default NewRoutineForm;
